const LogoIcon: FC<LogoIconProps> = ({ theme, className }) => {
  if (theme === "icon") {
    return <LogoSvg className={className} />;
  }

  const user = useUserStore((state) => state.user);
  const role = user?.role;

  if (theme === "text") {
    if (role === "BOSS") return <BossTextSvg className={className} />;
    if (role === "STAFF") return <StaffTextSvg className={className} />;
  }

  if (theme === "full") {
    if (role === "BOSS") return <BossFullSvg className={className} />;
    if (role === "STAFF") return <StaffFullSvg className={className} />;
  }

  return null;
};

export default LogoIcon;
